   output: 10
*/
double NO_INLINE sumOfMinimumElements(std::vector<double> const& x, std::vector<double> const& y) {
    std::vector<double> z;
    z.resize(x.size());
    std::transform(x.begin(), x.end(), y.begin(), z.begin(), [] (const auto &a, const auto &b) {
        return std::min(a, b);
    });
    return std::reduce(z.begin(), z.end());
}

