void dfs_helper(std::vector<int> const& A, int node, size_t N, std::vector<bool> &visited) {
   visited[node] = true;
   #pragma omp parallel for
   for (int i = 0; i < N; i += 1) {
      if (A[node * N + i] == 1 && !visited[i]) {
         dfs_helper(A, i, N, visited);
      }
   }
}

int componentCount(std::vector<int> const& A, size_t N) {
   std::vector<bool> visited(N, false);
   int count = 0;
   #pragma omp parallel for reduction(+:count)
   for (int i = 0; i < N; i += 1) {
      if (!visited[i]) {
         #pragma omp critical
         {
            dfs_helper(A, i, N, visited);
            count += 1;
         }
      }
   }
   return count;
}

