#include <omp.h>

/* Vector x contains values between 0 and 100, inclusive. Count the number of
   values in [0,10), [10, 20), [20, 30), ... and store the counts in `bins`.
   Example:
   input: [7, 32, 95, 12, 39, 32, 11, 71, 70, 66]
   output: [1, 2, 0, 3, 0, 0, 1, 2, 0, 1]
*/
void binsBy10Count(const std::vector<double>& x, std::array<size_t, 10>& bins) {
    // Initialize bins to zero
    bins.fill(0);

    // Parallel region to perform the counting operation
    #pragma omp parallel
    {
        std::array<size_t, 10> local_bins = {0};

        #pragma omp for nowait
        for (size_t i = 0; i < x.size(); ++i) {
            const size_t bin = static_cast<size_t>(x[i] / 10);
            local_bins[bin] += 1;
        }

        // Combine local counts to global bins using critical section
        #pragma omp critical
        {
            for (size_t j = 0; j < 10; ++j) {
                bins[j] += local_bins[j];
            }
        }
    }
}

