/* Count the number of pixels in image with each grayscale intensity.
   The vector `image` is a grayscale image with values 0-255.
   Store the results in `bins`.
   Example:

   input: image=[2, 116, 201, 11, 92, 92, 201, 4, 2]
   output: [0, 0, 2, 0, 1, ...]
*/
void pixelCounts(std::vector<int> const& image, std::array<size_t, 256> &bins) {
   // Initialize bins to 0 using OpenMP
   #pragma omp parallel for
   for (size_t i = 0; i < bins.size(); ++i) {
      bins[i] = 0;
   }

   // Use a local histogram for each thread
   std::array<size_t, 256> local_bins;
   #pragma omp parallel private(local_bins)
   {
       local_bins.fill(0);
       #pragma omp for
       for (size_t i = 0; i < image.size(); ++i) {
           local_bins[image[i]] += 1;
       }

       // Combine the results from local_bins into the global bins
       #pragma omp critical
       { 
           for (size_t i = 0; i < bins.size(); ++i) {
               bins[i] += local_bins[i];
           }
       }
   }
}

24_histogram_count_quartile:
