/* Count the number of doubles in the vector x that have a fractional part 
   in [0, 0.25), [0.25, 0.5), [0.5, 0.75), and [0.75, 1). Store the counts in `bins`.
   Examples:
   input: [7.8, 4.2, 9.1, 7.6, 0.27, 1.5, 3.8]
   output: [2, 1, 2, 2]
   input: [1.9, 0.2, 0.6, 10.1, 7.4]
   output: [2, 1, 1, 1]
*/
void countQuartiles(std::vector<double> const& x, std::array<size_t, 4> &bins) {
    // Initialize bins to zero
    bins.fill(0);

    // Parallelize the loop using OpenMP
    #pragma omp parallel
    {
        // Private array for thread-local counts
        std::array<size_t, 4> local_bins = {0, 0, 0, 0};

        #pragma omp for
        for (int i = 0; i < x.size(); i++) {
            const double frac = x[i] - (int)x[i];
            if (frac < 0.25) {
                local_bins[0] += 1;
            } else if (frac < 0.5) {
                local_bins[1] += 1;
            } else if (frac < 0.75) {
                local_bins[2] += 1;
            } else {
                local_bins[3] += 1;
            }
        }

        // Combine local counts into global bins
        #pragma omp critical
        {
            for (int j = 0; j < 4; j++) {
                bins[j] += local_bins[j];
            }
        }
    }
}

22_histogram_count_quadrants:
#pragma once
#include <array>
#include <vector>
#include <omp.h>

struct Point {
   double x, y;
};

/* Count the number of cartesian points in each quadrant in parallel. The vector points contains a list of `Point` objects.
   Store the counts in `bins`.
   Example:
   input: [{x=1.5, y=0.1}, {x=-3, y=1.1}, {x=5, y=9}, {x=1.5, y=-1}, {x=3, y=-7}, {x=0.1, y=2}]
   output: [3, 1, 0, 2]
*/
void countQuadrants(std::vector<Point> const& points, std::array<size_t, 4>& bins) {
    std::array<size_t, 4> local_bins = {0, 0, 0, 0};
    #pragma omp parallel
    {
        // Use a local array for each thread to avoid false sharing
        #pragma omp for
        for (size_t i = 0; i < points.size(); ++i) {
            const Point& point = points[i];
            if (point.x >= 0 && point.y >= 0) {
                local_bins[0]++;
            } else if (point.x < 0 && point.y >= 0) {
                local_bins[1]++;
            } else if (point.x < 0 && point.y < 0) {
                local_bins[2]++;
            } else if (point.x >= 0 && point.y < 0) {
                local_bins[3]++;
            }
        }

        // Combine results from all threads
        #pragma omp critical
        {
            bins[0] += local_bins[0];
            bins[1] += local_bins[1];
            bins[2] += local_bins[2];
            bins[3] += local_bins[3];
        }
    }
}

34_scan_largest_contiguous_subarray_sum:
#pragma once
#include <vector>
#include <limits>
#include <omp.h>

/* Return the largest sum of any contiguous subarray in the vector x.
   i.e. if x=[-2, 1, -3, 4, -1, 2, 1, -5, 4] then [4, -1, 2, 1] is the contiguous
   subarray with the largest sum of 6.
   Example:
   input: [-2, 1, -3, 4, -1, 2, 1, -5, 4]
   output: 6
*/
int maximumSubarray(std::vector<int> const& x) {
    int largestSum = std::numeric_limits<int>::lowest();
    int size = x.size();

    #pragma omp parallel
    {
        int localLargest = std::numeric_limits<int>::lowest();

        #pragma omp for nowait
        for (int i = 0; i < size; i++) {
            int currSum = 0;
            for (int j = i; j < size; j++) {
                currSum += x[j];
                if (currSum > localLargest) localLargest = currSum;
            }
        }

        #pragma omp critical
        {
            if (localLargest > largestSum) largestSum = localLargest;
        }
    }
    return largestSum;
}

33_scan_reverse_prefix_sum:
#pragma once
#include <numeric>
#include <vector>
#include <omp.h>

/* Compute the reverse prefix sum of the vector x into output.
   Examples:
   input: [1, 7, 4, 6, 6, 2]
   output: [2, 8, 14, 18, 25, 26]
   input: [3, 3, 7, 1, -2]
   output: [-2, -1, 6, 9, 12]
*/
void reversePrefixSum(std::vector<int> const& x, std::vector<int> &output) {
    int n = x.size();
    output.resize(n);

    // Parallel processing using OpenMP
    #pragma omp parallel for
    for (int i = 0; i < n; i++) {
        output[n - 1 - i] = x[i];
    }

    // Inclusive scan on the output vector
    std::inclusive_scan(output.begin(), output.end(), output.begin());
}

32_scan_sum_of_prefix_sum_array:
#pragma once
#include <numeric>
#include <vector>
#include <omp.h>

/* Compute the prefix sum array of the vector x and return its sum.
   Example:
   input: [-7, 2, 1, 9, 4, 8]
   output: 15
*/
double sumOfPrefixSum(std::vector<double> const& x) {
    size_t n = x.size();
    std::vector<double> prefixSum(n);

    // Parallelize the prefix sum calculation
    #pragma omp parallel
    {
        std::vector<double> localPrefixSum(n);

        // Create local prefix sums
        #pragma omp for
        for (size_t i = 0; i < n; ++i) {
            if (i == 0) {
                localPrefixSum[i] = x[i];
            } else {
                localPrefixSum[i] = localPrefixSum[i - 1] + x[i];
            }
        }

        // Combine local sums into the final prefix sum
        #pragma omp single
        {
            for (size_t i = 0; i < n; ++i) {
                prefixSum[i] = localPrefixSum[i];
            }
        }
    }

    // Sum the prefix sums in parallel
    double totalSum = 0.0;
    #pragma omp parallel for reduction(+:totalSum)
    for (size_t i = 0; i < n; ++i) {
        totalSum += prefixSum[i];
    }

    return totalSum;
}

