    for (int i = 0; i < n; ++i) {
        #pragma omp critical
        {
            min_val = std::min(min_val, x[i]);
            x[i] = min_val;
        }
    }
}

30_scan_prefix_sum:
#pragma once
#include <numeric>
#include <vector>
#include <omp.h>

/* Compute the prefix sum of the vector x into output.
   Example:
   input: [1, 7, 4, 6, 6, 2]
   output: [1, 8, 12, 18, 24, 26]
*/
void prefixSum(std::vector<double> const& x, std::vector<double> &output) {
    size_t n = x.size();
    if (n == 0) return;
    output[0] = x[0];

    #pragma omp parallel
    {
        std::vector<double> temp(n);
        #pragma omp for
        for (size_t i = 1; i < n; ++i) {
            temp[i] = output[i-1] + x[i];
        }

        #pragma omp critical
        {
            for (size_t i = 1; i < n; ++i) {
                output[i] = temp[i];
            }
        }
    }
}

