48_sparse_la_sparse_axpy:
#pragma once
#include <vector>
#include <omp.h>

struct Element {
	size_t index;
	double value;
};

/* Compute z = alpha*x+y where x and y are sparse vectors. Store the result in z.
   Example:

   input: x=[{5, 12}, {8, 3}, {12, -1}], y=[{3, 1}, {5, -2}, {7, 1}, {8, -3}], alpha=1
   output: z=[{3, 1}, {5, 10}, {7, 1}, {12, -1}]
*/
void sparseAxpy(double alpha, std::vector<Element> const& x, std::vector<Element> const& y, std::vector<double> &z) {
	#pragma omp parallel
	{
		#pragma omp for
		for(size_t i = 0; i < z.size(); i++) {
			z[i] = 0.0;
		}
	}

	size_t xi = 0, yi = 0;
	while (xi < x.size() && yi < y.size()) {
		if (x[xi].index < y[yi].index) {
			// Only x contributes
			#pragma omp atomic
			z[x[xi].index] += alpha * x[xi].value;
			++xi;
		} else if (x[xi].index > y[yi].index) {
			// Only y contributes
			#pragma omp atomic
			z[y[yi].index] += y[yi].value;
			++yi;
		} else {
			// Both x and y contribute
			#pragma omp atomic
			z[x[xi].index] += alpha * x[xi].value + y[yi].value;
			++xi;
			++yi;
		}
	}
	#pragma omp parallel
	{
		#pragma omp for
		for(; xi < x.size(); ++xi) {
			#pragma omp atomic
			z[x[xi].index] += alpha * x[xi].value;
		}
	}
	#pragma omp parallel
	{
		#pragma omp for
		for(; yi < y.size(); ++yi) {
			#pragma omp atomic
			z[y[yi].index] += y[yi].value;
		}
	}
}

49_sparse_la_sparse_lu_decomp:
#pragma once
#include <vector>
#include <omp.h>

struct COOElement {
   size_t row, column;
   double value;
};

void luFactorize(std::vector<COOElement> const& A, std::vector<double> &L, std::vector<double> &U, size_t N) {
   std::vector<std::vector<double>> fullA(N, std::vector<double>(N, 0));

   // Fill the full matrix from COO format
   for (const auto& element : A) {
      fullA[element.row][element.column] = element.value;
   }

   // LU factorization algorithm with OpenMP
   #pragma omp parallel for
   for (size_t i = 0; i < N; ++i) {
      for (size_t j = 0; j < N; ++j) {
         if (j >= i) {
               U[i * N + j] = fullA[i][j];
               for (size_t k = 0; k < i; ++k) {
                  U[i * N + j] -= L[i * N + k] * U[k * N + j];
               }
         }
         if (i > j) {
               L[i * N + j] = fullA[i][j] / U[j * N + j];
               for (size_t k = 0; k < j; ++k) {
                  L[i * N + j] -= L[i * N + k] * U[k * N + j] / U[j * N + j];
               }
         }
      }
      L[i * N + i] = 1;
   }
}

46_sparse_la_spmm:
#pragma once
#include <vector>
#include <omp.h>

struct COOElement {
   size_t row, column;
   double value;
};

/* Compute the matrix multiplication Y=AX. A is a sparse MxK matrix in COO format.
   X is a sparse KxN matrix in COO format. Y is a dense MxN matrix in row-major.
   Example:
   input: A=[{0,0,-2}, {0,1,1}, {1,1,-1}] X=[{0,1,2}, {1,0,-1}]
   output: Y=[{-1,-4}, {1,0}]
*/
void spmm(const std::vector<COOElement>& A, const std::vector<COOElement>& X, std::vector<double>& Y, size_t M, size_t K, size_t N) {
   Y.assign(M * N, 0);
   #pragma omp parallel for
   for (size_t i = 0; i < A.size(); i++) {
       const auto& a = A[i];
       for (size_t j = 0; j < X.size(); j++) {
           const auto& x = X[j];
           if (a.column == x.row) {
               #pragma omp atomic
               Y[a.row * N + x.column] += a.value * x.value;
           }
       }
   }
}

