   double value;
};

void solveLinearSystem(std::vector<COOElement> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
   std::vector<std::vector<double>> matrix(N, std::vector<double>(N, 0.0));   
   std::vector<double> b_copy = b;

   // Fill the matrix with the values from A
   for (const auto& element : A) {
      matrix[element.row][element.column] = element.value;
   }

   x.assign(N, 0.0);

   // Perform Gaussian elimination using OpenMP
   #pragma omp parallel
   {
       // Pivoting
       for (size_t i = 0; i < N; ++i) {
           double maxEl = std::abs(matrix[i][i]);
           size_t maxRow = i;
           for (size_t k = i + 1; k < N; ++k) {
               if (std::abs(matrix[k][i]) > maxEl) {
                   maxEl = std::abs(matrix[k][i]);
                   maxRow = k;
               }
           }
           #pragma omp single
           {
               // Swap maximum row with current row (column by column)
               for (size_t k = i; k < N; ++k) {
                   std::swap(matrix[maxRow][k], matrix[i][k]);
               }
               std::swap(b_copy[maxRow], b_copy[i]);
           }

           // Eliminate rows below pivot
           #pragma omp for
           for (size_t k = i + 1; k < N; ++k) {
               double c = -matrix[k][i] / matrix[i][i];
               for (size_t j = i; j < N; ++j) {
                   if (i == j) {
                       matrix[k][j] = 0;
                   } else {
                       matrix[k][j] += c * matrix[i][j];
                   }
               }
               b_copy[k] += c * b_copy[i];
           }
       }
   }

   // Solve the upper triangular matrix A using backward substitution
   for (int i = N - 1; i >= 0; --i) {
       x[i] = b_copy[i] / matrix[i][i];
       #pragma omp parallel for
       for (int k = 0; k < i; ++k) {
           #pragma omp atomic
           b_copy[k] -= matrix[k][i] * x[i];
       }
   }
}

47_sparse_la_spmv:
#pragma once
#include <vector>
#include <omp.h>

struct COOElement {
   size_t row, column;
   double value;
};

/* Compute y = alpha*A*x + beta*y where alpha and beta are scalars, x and y are vectors,
   and A is a sparse matrix stored in COO format.
   x and y are length N and A is M x N.
   Example:
   input: alpha=0.5 beta=1.0 A=[{0,1,3}, {1,0,-1}] x=[-4, 2] y=[-1,1]
   output: y=[2, 3]
*/
void spmv(double alpha, std::vector<COOElement> const& A, std::vector<double> const& x, double beta, std::vector<double> &y, size_t M, size_t N) {
   // Update y with beta in parallel
   #pragma omp parallel for
   for (size_t i = 0; i < y.size(); ++i) {
      y[i] *= beta;
   }

   // Perform sparse matrix-vector multiplication in parallel
   #pragma omp parallel for
   for (size_t i = 0; i < A.size(); ++i) {
      const auto& a_ij = A[i];
      if (a_ij.row < M && a_ij.column < N) {
         #pragma omp atomic
         y[a_ij.row] += alpha * a_ij.value * x[a_ij.column];
      }
   }
}

