   output: true
*/
bool reduceLogicalXOR(std::vector<bool> const& x) {
    bool result = false;
    int n = x.size();

    #pragma omp parallel
    {
        bool local_result = false;

        #pragma omp for
        for (int i = 0; i < n; ++i) {
            local_result ^= x[i]; // XOR operation
        }

        #pragma omp critical
        { 
            result ^= local_result; // Combine results from threads
        }
    }
    return result;
}

