#include <omp.h>

#if !defined(M_PI)
#define M_PI 3.14159265358979323846
#endif

void fft(std::vector<std::complex<double>> const& x, std::vector<std::complex<double>> &output) {
    output = x;
    unsigned int N = output.size(), k = N, n;
    double thetaT = M_PI / N;
    std::complex<double> phiT = std::complex<double>(std::cos(thetaT), -std::sin(thetaT)), T;

    while (k > 1) {
        n = k;
        k >>= 1;
        phiT *= phiT;
        T = 1.0L;

        #pragma omp parallel for private(T) schedule(static)
        for (unsigned int l = 0; l < k; l++) {
            for (unsigned int a = l; a < N; a += n) {
                unsigned int b = a + k;
                std::complex<double> t = output[a] - output[b];
                output[a] += output[b];
                output[b] = t * T;
            }
            T *= phiT;
        }
    }

    unsigned int m = (unsigned int)std::log2(N);
    #pragma omp parallel for
    for (unsigned int a = 0; a < N; a++) {
        unsigned int b = a;
        // Reverse bits
        b = (((b & 0xaaaaaaaa) >> 1) | ((b & 0x55555555) << 1));
        b = (((b & 0xcccccccc) >> 2) | ((b & 0x33333333) << 2));
        b = (((b & 0xf0f0f0f0) >> 4) | ((b & 0x0f0f0f0f) << 4));
        b = (((b & 0xff00ff00) >> 8) | ((b & 0x00ff00ff) << 8));
        b = ((b >> 16) | (b << 16)) >> (32 - m);
        if (b > a) {
            std::complex<double> t = output[a];
            output[a] = output[b];
            output[b] = t;
        }
    }
}

07_fft_fft_conjugate:
#pragma once
#include <vector>
#include <complex>
#include <cmath>
#include <omp.h>

void fftConjugate(std::vector<std::complex<double>>& x) {
    const size_t N = x.size();
    if (N <= 1) return;
    // divide
    std::vector<std::complex<double>> even(N / 2);
    std::vector<std::complex<double>> odd(N / 2);
    #pragma omp parallel for
    for (size_t i = 0; i < N / 2; ++i) {
        even[i] = x[i * 2];
        odd[i] = x[i * 2 + 1];
    }
    // conquer
    fftCooleyTookey(even);
    fftCooleyTookey(odd);
    // combine
    #pragma omp parallel for
    for (size_t k = 0; k < N / 2; ++k) {
        std::complex<double> t = std::polar(1.0, -2 * M_PI * k / N) * odd[k];
        x[k    ] = even[k] + t;
        x[k + N / 2] = even[k] - t;
    }
    // conjugate
    #pragma omp parallel for
    for (size_t i = 0; i < x.size(); i += 1) {
        x[i] = std::conj(x[i]);
    }
}

05_fft_inverse_fft:
#pragma once
#include <cmath>
#include <vector>
#include <complex>
#include <algorithm>
#include <omp.h>

#if !defined(M_PI)
#define M_PI 3.14159265358979323846
#endif

void fft_helper(std::vector<std::complex<double>> &x) {
   unsigned int N = x.size(), k = N, n;
   double thetaT = M_PI / N;
   std::complex<double> phiT = std::complex<double>(std::cos(thetaT), -std::sin(thetaT)), T;

   while (k > 1) {
       n = k;
       k >>= 1;
       phiT *= phiT;
       T = 1.0;
       #pragma omp parallel for
       for (unsigned int l = 0; l < k; l++) {
           for (unsigned int a = l; a < N; a += n) {
               unsigned int b = a + k;
               std::complex<double> t = x[a] - x[b];
               x[a] += x[b];
               x[b] = t * T;
           }
           T *= phiT;
       }
   }

   unsigned int m = (unsigned int)std::log2(N);
   #pragma omp parallel for
   for (unsigned int a = 0; a < N; a++) {
       unsigned int b = a;
       b = (((b & 0xaaaaaaaa) >> 1) | ((b & 0x55555555) << 1));
       b = (((b & 0xcccccccc) >> 2) | ((b & 0x33333333) << 2));
       b = (((b & 0xf0f0f0f0) >> 4) | ((b & 0x0f0f0f0f) << 4));
       b = (((b & 0xff00ff00) >> 8) | ((b & 0x00ff00ff) << 8));
       b = ((b >> 16) | (b << 16)) >> (32 - m);
       if (b > a) {
           std::complex<double> t = x[a];
           x[a] = x[b];
           x[b] = t;
       }
   }
}

void ifft(std::vector<std::complex<double>> &x) {
   std::transform(x.begin(), x.end(), x.begin(), [](const auto& val) { return std::conj(val); });
   fft_helper(x);
   std::transform(x.begin(), x.end(), x.begin(), [](const auto& val) { return std::conj(val); });
   double size_inv = 1.0 / static_cast<double>(x.size());
   #pragma omp parallel for
   for (size_t i = 0; i < x.size(); i++) {
       x[i] *= size_inv;
   }
}

