};

double distance(Point const& p1, Point const& p2) {
	return std::sqrt((p2.x - p1.x) * (p2.x - p1.x) + (p2.y - p1.y) * (p2.y - p1.y));
}

/* Return the distance between the closest two points in the vector points.
   Example:
   input: [{2, 3}, {12, 30}, {40, 50}, {5, 1}, {12, 10}, {3, 4}]
   output: 1.41421
*/
double closestPair(std::vector<Point> const& points) {
    // The polygon needs to have at least two points
    if (points.size() < 2) {
        return 0;
    }
    double minDist = std::numeric_limits<double>::max();

    // Parallelizing the nested loop with OpenMP
    #pragma omp parallel for reduction(min:minDist)
    for (size_t i = 0; i < points.size() - 1; i++) {
        for (size_t j = i + 1; j < points.size(); j++) {
            const double dist = distance(points[i], points[j]);
            if (dist < minDist) {
                minDist = dist;
            }
        }
    }
    return minDist;
}

