11_geometry_convex_hull_perimeter:
#pragma once
#include <vector>
#include <algorithm>
#include <cmath>
#include <omp.h>

struct Point {
	double x, y;
};

double distance(Point const& p1, Point const& p2) {
	return std::hypot(p2.x - p1.x, p2.y - p1.y);
}

double convexHullPerimeter(std::vector<Point> const& points) {
	if (points.size() < 3) {
		return 0;
	}
	std::vector<Point> pointsSorted = points;
	std::sort(pointsSorted.begin(), pointsSorted.end(), [](Point const& a, Point const& b) {
		return a.x < b.x || (a.x == b.x && a.y < b.y);
	});

auto CrossProduct = [](Point const& a, Point const& b, Point const& c) {
		return (c.x - a.x) * (b.y - a.y) - (c.y - a.y) * (b.x - a.x) > 0;
	};

	std::vector<Point> upperHull;
	std::vector<Point> lowerHull;

	upperHull.push_back(pointsSorted[0]);
	upperHull.push_back(pointsSorted[1]);

	#pragma omp parallel
	{
		std::vector<Point> upperTemp;
		std::vector<Point> lowerTemp;
		#pragma omp for nowait
		for (size_t i = 2; i < pointsSorted.size(); i++) {
			while (upperHull.size() > 1 && !CrossProduct(upperHull[upperHull.size() - 2], upperHull[upperHull.size() - 1], pointsSorted[i])) {
				upperHull.pop_back();
			}
			upperHull.push_back(pointsSorted[i]);
			while (lowerHull.size() > 1 && !CrossProduct(lowerHull[lowerHull.size() - 2], lowerHull[lowerHull.size() - 1], pointsSorted[pointsSorted.size() - i - 1])) {
				lowerHull.pop_back();
			}
			lowerHull.push_back(pointsSorted[pointsSorted.size() - i - 1]);
		}
	}

	upperHull.insert(upperHull.end(), lowerHull.begin(), lowerHull.end());
	double perimeter = 0;
	#pragma omp parallel for reduction(+:perimeter)
	for (size_t i = 0; i < upperHull.size(); i++) {
		perimeter += distance(upperHull[i], upperHull[(i + 1) % upperHull.size()]);
	}
	return perimeter;
}

10_geometry_convex_hull:
#pragma once
#include <vector>
#include <algorithm>
#include <omp.h>

struct Point {
    double x, y;
};

/* Find the set of points that defined the smallest convex polygon that contains all the points in the vector points. Store the result in `hull`.
   Example:
   input: [{0, 3}, {1, 1}, {2, 2}, {4, 4}, {0, 0}, {1, 2}, {3, 1}, {3, 3}]
   output: [{0, 3}, {4, 4}, {3, 1}, {0, 0}]
*/
void convexHull(std::vector<Point> const& points, std::vector<Point>& hull) {
    // The polygon needs to have at least three points
    size_t num_points = points.size();
    if (num_points < 3) {
        hull = points;
        return;
    }

    std::vector<Point> pointsSorted = points;
    std::sort(pointsSorted.begin(), pointsSorted.end(), [](Point const& a, Point const& b) {
        return a.x < b.x || (a.x == b.x && a.y < b.y);
    });

    auto CrossProduct = [](Point const& a, Point const& b, Point const& c) {
        return (c.x - a.x) * (b.y - a.y) - (c.y - a.y) * (b.x - a.x) > 0;
    };

    std::vector<Point> upperHull;
    std::vector<Point> lowerHull;
    upperHull.reserve(num_points);
    lowerHull.reserve(num_points);

    // Build upper hull
    upperHull.push_back(pointsSorted[0]);
    upperHull.push_back(pointsSorted[1]);
    for (size_t i = 2; i < num_points; i++) {
        while (upperHull.size() > 1 && !CrossProduct(upperHull[upperHull.size() - 2], upperHull.back(), pointsSorted[i])) {
            upperHull.pop_back();
        }
        upperHull.push_back(pointsSorted[i]);
    }

    // Build lower hull
    lowerHull.push_back(pointsSorted[num_points - 1]);
    lowerHull.push_back(pointsSorted[num_points - 2]);
    for (size_t i = num_points - 3; i < num_points; i--) {
        while (lowerHull.size() > 1 && !CrossProduct(lowerHull[lowerHull.size() - 2], lowerHull.back(), pointsSorted[i])) {
            lowerHull.pop_back();
        }
        lowerHull.push_back(pointsSorted[i]);
    }

    // Combine both hulls
    upperHull.insert(upperHull.end(), lowerHull.rbegin() + 1, lowerHull.rend());
    hull = std::move(upperHull);
}

