/* Compute z = alpha*x+y where x and y are vectors. Store the result in z.
   Example:

   input: x=[1, -5, 2, 9] y=[0, 4, 1, -1] alpha=2
   output: z=[2, -6, 5, 17]
*/
void axpy(double alpha, std::vector<double> const& x, std::vector<double> const& y, std::vector<double> &z) {
   size_t n = x.size();
   #pragma omp parallel for
   for (size_t i = 0; i < n; i += 1) {
      z[i] = alpha * x[i] + y[i];
   }
}

02_dense_la_gemm:
#pragma once
#include <vector>
#include <omp.h>

/* Multiply the matrix A by the matrix B. Store the results in the matrix C.
   A is an MxK matrix, B is a KxN matrix, and C is a MxN matrix. The matrices are stored in row-major.
   Example:
   input: A=[[1, -1, 2], [0, -2, 1]] B=[[4, 1], [-1, 0], [2, 2]]
   output: C=[[9, 5], [4, 2]]
*/
void gemm(const std::vector<double> &A, const std::vector<double> &B, std::vector<double> &C, size_t M, size_t K, size_t N) {
    #pragma omp parallel for collapse(2) // Parallelize the outer two loops 
    for (size_t i = 0; i < M; i++) {
        for (size_t j = 0; j < N; j++) {
            double sum = 0.0;
            for (size_t k = 0; k < K; k++) {
                sum += A[i*K + k] * B[k*N + j];
            }
            C[i*N + j] = sum;
        }
    }
}

04_dense_la_gemv:
