/* Multiply the matrix A by the vector x. Store the results in the vector y.
   A is an MxN matrix stored in row-major, x has N elements, and y has M elements.
   Example:
   input: A=[[1, -1, 2], [0, -3, 1]] x=[2, 1, 0]
   output: y=[1, -3]
*/
void gemv(std::vector<double> const& A, std::vector<double> const& x, std::vector<double> &y, size_t M, size_t N) {
   #pragma omp parallel for
   for (size_t i = 0; i < M; i++) {
      double sum = 0.0;
      for (size_t j = 0; j < N; j++) {
         sum += A[i * N + j] * x[j];
      }
      y[i] = sum;
   }
}

01_dense_la_solve:
#pragma once
#include <vector>
#include <omp.h>

/* Solve the linear system Ax=b for x.
   A is an NxN matrix in row-major. x and b have N elements.
   Example:

   input: A=[[1,4,2], [1,2,3], [2,1,3]] b=[11, 11, 13]
   output: x=[3, 1, 2]
*/
void solveLinearSystem(std::vector<double> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
   // Create a copy of A to perform Gaussian elimination
   std::vector<double> A_copy = A;
   std::vector<double> b_copy = b;

   // Gaussian elimination
   for (size_t i = 0; i < N - 1; i++) {
      // Find the pivot element
      double pivot = A_copy[i * N + i];
      // Check if the pivot is zero
      if (pivot == 0) {
         return;
      }
      // Eliminate the elements below the pivot using parallelization
      #pragma omp parallel for schedule(dynamic)
      for (size_t j = i + 1; j < N; j++) {
         double factor = A_copy[j * N + i] / pivot;
         for (size_t k = i; k < N; k++) {
            A_copy[j * N + k] -= factor * A_copy[i * N + k];
         }
         b_copy[j] -= factor * b_copy[i];
      }
   }
   // Back substitution (parallelize this as well except for the last index)
   #pragma omp parallel for
   for (int i = N - 1; i >= 0; i--) {
      double sum = 0;
      for (size_t j = i + 1; j < N; j++) {
         sum += A_copy[i * N + j] * x[j];
      }
      x[i] = (b_copy[i] - sum) / A_copy[i * N + i];
   }
}

00_dense_la_lu_decomp:
#pragma once
#include <vector>
#include <omp.h>

/* Factorize the matrix A into A=LU where L is a lower triangular matrix and U is an upper triangular matrix.
   Store the results for L and U into the original matrix A. 
   A is an NxN matrix stored in row-major.
   Example:
   input: [[4, 3], [6, 3]]
   output: [[4, 3], [1.5, -1.5]]
*/
void luFactorize(std::vector<double> &A, size_t N) {
    #pragma omp parallel for
    for (size_t k = 0; k < N; ++k) {
        for (size_t i = k + 1; i < N; ++i) {
            double factor = A[i * N + k] / A[k * N + k];
            A[i * N + k] = factor;

            for (size_t j = k + 1; j < N; ++j) {
                A[i * N + j] -= factor * A[k * N + j];
            }
        }
    }
}

42_sort_sorted_ranks:
#pragma once
#include <algorithm>
#include <numeric>
#include <vector>
#include <omp.h>

/* For each value in the vector x compute its index in the sorted vector.
   Store the results in `ranks`.
   Examples:
   input: [3.1, 2.8, 9.1, 0.4, 3.14]
   output: [2, 1, 4, 0, 3]
   input: [100, 7.6, 16.1, 18, 7.6]
   output: [4, 0, 1, 2, 3]
*/
void ranks(std::vector<float> const& x, std::vector<size_t> &ranks_) {
   size_t n = x.size();
   std::vector<size_t> indices(n);
   std::iota(indices.begin(), indices.end(), 0);

   // Use parallel sort from OpenMP
   #pragma omp parallel
   {
       std::sort(indices.begin(), indices.end(), [&x](size_t i1, size_t i2) {
           return x[i1] < x[i2];
       });
   }

   // This section cannot be parallelized easily since we set ranks in order
   for (size_t i = 0; i < n; i++) {
       ranks_[indices[i]] = i;
   }
}

43_sort_sort_an_array_of_structs_by_key:
#pragma once
#include <vector>
#include <algorithm>
#include <omp.h>

struct Result {
   int startTime, duration;
   float value;
};

// Sort vector of Result structs by start time in ascending order.
// This implementation utilizes OpenMP for parallel sorting.
void sortByStartTime(std::vector<Result> &results) {
   int n = results.size();
   if (n <= 1) return;

   // Step 1: Create an array of indices
   std::vector<int> indices(n);
   #pragma omp parallel for
   for (int i = 0; i < n; ++i) {
       indices[i] = i;
   }

   // Step 2: Sort indices based on startTime using a parallel version of std::sort
   std::sort(indices.begin(), indices.end(), [&results](int a, int b) {
       return results[a].startTime < results[b].startTime;
   });

   // Step 3: Create a temporary vector to hold sorted results
   std::vector<Result> sortedResults(n);
   #pragma omp parallel for
   for (int i = 0; i < n; ++i) {
       sortedResults[i] = results[indices[i]];
   }

   // Step 4: Copy back to original vector
   results = std::move(sortedResults);
}

40_sort_sort_an_array_of_complex_numbers:
#pragma once
#include <complex>
#include <vector>
#include <algorithm>
#include <omp.h>

/* Sort the vector x of complex numbers by their magnitude in ascending order.
   Example:

   input: [3.0-1.0i, 4.5+2.1i, 0.0-1.0i, 1.0-0.0i, 0.5+0.5i]
   output: [0.5+0.5i, 0.0-1.0i, 1.0-0.0i, 3.0-1.0i, 4.5+2.1i]
*/
void sortComplexByMagnitude(std::vector<std::complex<double>> &x) {
    #pragma omp parallel
    {
        std::vector<std::complex<double>> local_x;
        #pragma omp single
        {
            local_x = x;
        }
        #pragma omp task
        std::sort(local_x.begin(), local_x.end(), [](const std::complex<double> &a, const std::complex<double> &b) {
            return std::abs(a) < std::abs(b);
        });
        #pragma omp taskwait
        #pragma omp critical
        {
            x = local_x;
        }
    }
}

