37_search_find_the_closest_number_to_pi:
#pragma once
#include <vector>
#include <cmath>
#include <limits>
#include <omp.h>

/* Return the index of the value in the vector x that is closest to the math constant PI.
   Use M_PI for the value of PI.
   Example:
   input: [9.18, 3.05, 7.24, 11.3, -166.49, 2.1]
   output: 1
*/
size_t findClosestToPi(std::vector<double> const& x) {
    size_t closest_index = 0;
    double closest_value = std::abs(x[0] - M_PI);
    size_t size = x.size();

    #pragma omp parallel
    {
        size_t private_index = 0;
        double private_min = std::numeric_limits<double>::max();

        #pragma omp for
        for (size_t i = 0; i < size; ++i) {
            double diff = std::abs(x[i] - M_PI);
            if (diff < private_min) {
                private_min = diff;
                private_index = i;
            }
        }

        #pragma omp critical
        {
            if (private_min < closest_value) {
                closest_value = private_min;
                closest_index = private_index;
            }
        }
    }
    return closest_index;
}

39_search_xor_contains:
#pragma once
#include <vector>
#include <algorithm>
#include <omp.h>

/* Return true if `val` is only in one of vectors x or y.
   Return false if it is in both or neither.
   Examples:
   input: x=[1,8,4,3,2], y=[3,4,4,1,1,7], val=7
   output: true
   input: x=[1,8,4,3,2], y=[3,4,4,1,1,7], val=1
   output: false
*/
bool xorContains(std::vector<int> const& x, std::vector<int> const& y, int val) {
    bool foundInX = false;
    bool foundInY = false;

    #pragma omp parallel
    {
        #pragma omp single
        {
            #pragma omp task shared(foundInX)
            {
                foundInX = std::find(x.begin(), x.end(), val) != x.end();
            }
            #pragma omp task shared(foundInY)
            {
                foundInY = std::find(y.begin(), y.end(), val) != y.end();
            }
        }
    }

    return foundInX ^ foundInY;
}

38_search_find_the_first_even_number:
#pragma once
#include <vector>
#include <omp.h>

/* Return the index of the first even number in the vector x.
   Examples:
   input: [7, 3, 9, 5, 5, 7, 2, 9, 12, 11]
   output: 6
   input: [3, 8, 9, 9, 3, 4, 8, 6]
   output: 1
*/
size_t findFirstEven(std::vector<int> const& x) {
    size_t index = x.size(); // Default to the size if not found
    #pragma omp parallel
    {
        size_t local_index = x.size();
        #pragma omp for
        for (size_t i = 0; i < x.size(); i++) {
            if (x[i] % 2 == 0 && i < local_index) {
                local_index = i;
            }
        }
        #pragma omp critical
        {
            if (local_index < index) {
                index = local_index;
            }
        }
    }
    return index;
}

35_search_search_for_last_struct_by_key:
#pragma once
#include <vector>
#include <string>
#include <omp.h>

struct Book {
   std::string title;
   int pages;
};

/* Return the index of the last Book item in the vector books where Book.pages is less than 100.
   Example:

   input: [{title=\"Green Eggs and Ham\", pages=72}, {title=\"gulliver's travels\", pages=362}, {title=\"Stories of Your Life\", pages=54}, {title=\"Hamilton\", pages=818}]
   output: 2
*/
size_t findLastShortBook(std::vector<Book> const& books) {
    size_t lastIndex = books.size();
    #pragma omp parallel for
    for (int i = books.size() - 1; i >= 0; i--) {
        if (books[i].pages < 100) {
            #pragma omp critical
            {
                if (i < lastIndex) {
                    lastIndex = i;
                }
            }
        }
    }
    return lastIndex;
}

36_search_check_if_array_contains_value:
#pragma once
