	double value;
};

/* Compute z = alpha*x+y where x and y are sparse vectors. Store the result in z.
   Example:

   input: x=[{5, 12}, {8, 3}, {12, -1}], y=[{3, 1}, {5, -2}, {7, 1}, {8, -3}], alpha=1
   output: z=[{3, 1}, {5, 10}, {7, 1}, {12, -1}]
*/
void sparseAxpy(double alpha, const std::vector<Element>& x, const std::vector<Element>& y, std::vector<double>& z) {
    size_t xi = 0, yi = 0;
    while (xi < x.size() && yi < y.size()) {
        if (x[xi].index < y[yi].index) {
            z[x[xi].index] += alpha * x[xi].value;
            ++xi;
        } else if (x[xi].index > y[yi].index) {
            z[y[yi].index] += y[yi].value;
            ++yi;
        } else {
            z[x[xi].index] += alpha * x[xi].value + y[yi].value;
            ++xi;
            ++yi;
        }
    }
    // Use a loop to handle remaining elements in x
    for (; xi < x.size(); ++xi) {
        z[x[xi].index] += alpha * x[xi].value;
    }
    // Use a loop to handle remaining elements in y
    for (; yi < y.size(); ++yi) {
        z[y[yi].index] += y[yi].value;
    }
}

49_sparse_la_sparse_lu_decomp:
#pragma once
#include <vector>
#include <omp.h>

struct COOElement {
   size_t row, column;
   double value;
};

/* Factorize the sparse matrix A into A=LU where L is a lower triangular matrix and U is an upper triangular matrix.
   A is a sparse NxN matrix stored in COO format. L and U are NxN matrices in row-major.
   Example:
   input: A=[{0,0,4}, {0,1,3}, {1,0,6}, {1,1,3}]
   output: L=[{0,0,1},{1,0,1.5}, {1,1,1}] U=[{0,0,4}, {0,1,3}, {1,1,-1.5}]
*/
void luFactorize(std::vector<COOElement> const& A, std::vector<double> &L, std::vector<double> &U, size_t N) {
   std::vector<std::vector<double>> fullA(N, std::vector<double>(N, 0));
   for (const auto& element : A) {
      fullA[element.row][element.column] = element.value;
   }

   // LU factorization algorithm
   #pragma omp parallel for collapse(2)
   for (size_t i = 0; i < N; ++i) {
      for (size_t j = 0; j < N; ++j) {
         if (j >= i) {
               U[i * N + j] = fullA[i][j];
               for (size_t k = 0; k < i; ++k) {
                  U[i * N + j] -= L[i * N + k] * U[k * N + j];
               }
         }
         if (i > j) {
               L[i * N + j] = fullA[i][j] / U[j * N + j];
               for (size_t k = 0; k < j; ++k) {
                  L[i * N + j] -= L[i * N + k] * U[k * N + j] / U[j * N + j];
               }
         }
      }
      L[i * N + i] = 1;
   }
}

