   std::unordered_map<size_t, double> x_map;
   for (const auto& x : X) {
       x_map[x.row * N + x.column] += x.value;
   }
   for (const auto& a : A) {
       size_t key = a.row * N;
       for (size_t j = 0; j < N; ++j) {
           Y[key + j] += a.value * x_map[key + j];
       }
   }
}

45_sparse_la_sparse_solve:
#pragma once
#include <vector>
#include <algorithm>
#include <cassert>

struct COOElement {
   size_t row, column;
   double value;
};

/* Solve the sparse linear system Ax=b for x.
   A is a sparse NxN matrix in COO format. x and b are dense vectors with N elements.
   Example:

   input: A=[{0,0,1}, {0,1,1}, {1,1,-2}] b=[1,4]
   output: x=[3,-2]
*/
void solveLinearSystem(std::vector<COOElement> const& A, std::vector<double> const& b, std::vector<double> &x, size_t N) {
   std::vector<std::vector<double>> matrix(N, std::vector<double>(N, 0.0));   
   std::vector<double> b_copy = b;
   // Fill the matrix with the values from A
   for (const auto& element : A) {
      matrix[element.row][element.column] = element.value;
   }
   // Initialize x with the size N and fill with zero
   x.assign(N, 0.0);
   // Perform Gaussian elimination
   for (size_t i = 0; i < N; ++i) {
      // Find pivot
      size_t maxRow = i;
      for (size_t k = i + 1; k < N; ++k) {
         if (std::abs(matrix[k][i]) > std::abs(matrix[maxRow][i])) {
               maxRow = k;
         }
      }
      std::swap(matrix[maxRow], matrix[i]);
      std::swap(b_copy[maxRow], b_copy[i]);
      assert(matrix[i][i] != 0); // Ensure the pivot element is not zero
      for (size_t k = i + 1; k < N; ++k) {
         double c = -matrix[k][i] / matrix[i][i];
         for (size_t j = i; j < N; ++j) {
            matrix[k][j] += c * matrix[i][j];
         }
         b_copy[k] += c * b_copy[i];
      }
   }
   // Solve equation Ax=b for an upper triangular matrix A
   for (int i = N - 1; i >= 0; --i) {
      x[i] = b_copy[i] / matrix[i][i];
      for (int k = i - 1; k >= 0; --k) {
         b_copy[k] -= matrix[k][i] * x[i];
      }
   }
}

