*/
void spmv(double alpha, const std::vector<COOElement>& A, const std::vector<double>& x, double beta, std::vector<double>& y, size_t M, size_t N) {
   // Optimize with reserve to avoid reallocations
   y.reserve(M);
   for (auto& element : y) {
      element *= beta;
   }

   for (const auto& a_ij : A) {
      if (a_ij.row < M && a_ij.column < N) {
         y[a_ij.row] += alpha * a_ij.value * x[a_ij.column];
      }
   }
}

