double convexHullPerimeter(const std::vector<Point>& points) {
    if (points.size() < 3) {
        return 0;
    }
    std::vector<Point> pointsSorted(points);
    std::sort(pointsSorted.begin(), pointsSorted.end(), [](const Point& a, const Point& b) {
        return a.x < b.x || (a.x == b.x && a.y < b.y);
    });
    auto crossProduct = [](const Point& a, const Point& b, const Point& c) {
        return (b.x - a.x) * (c.y - a.y) - (b.y - a.y) * (c.x - a.x);
    };
    std::vector<Point> hull;
    for (const auto& point : pointsSorted) {
        while (hull.size() >= 2 && crossProduct(hull[hull.size() - 2], hull.back(), point) <= 0) {
            hull.pop_back();
        }
        hull.push_back(point);
    }
    size_t hullSize = hull.size();
    for (size_t i = pointsSorted.size() - 1; i > 0; --i) {
        while (hull.size() > hullSize && crossProduct(hull[hull.size() - 2], hull.back(), pointsSorted[i - 1]) <= 0) {
            hull.pop_back();
        }
        hull.push_back(pointsSorted[i - 1]);
    }
    double perimeter = 0;
    for (size_t i = 0; i < hull.size(); ++i) {
        perimeter += distance(hull[i], hull[(i + 1) % hull.size()]);
    }
    return perimeter;
}

