    for (size_t i = 0; i < N; i++) {
        for (size_t j = 0; j < N; j++){
            int sum = 0;
            for (int k = -1; k <= 1; k++) {
                for (int l = -1; l <= 1; l++){
                    int x = i + k;
                    int y = j + l;
                    if (x >= 0 && x < N && y >= 0 && y < N) {
                        sum += imageIn[x * N + y] * edgeKernel[k + 1][l + 1];
                    }
                }
            }
            imageOut[i * N + j] = std::clamp(sum, 0, 255); // Clamping the result
