    for (size_t i = 0; i < N - 1; i++) {
        // Find the pivot element
        double pivot = A_copy[i * N + i];
        if (pivot == 0) {
            throw std::runtime_error("Matrix is singular and cannot be solved.");
        }

        // Eliminate the elements below the pivot
        for (size_t j = i + 1; j < N; j++) {
            double factor = A_copy[j * N + i] / pivot;
            for (size_t k = i; k < N; k++) {
                A_copy[j * N + k] -= factor * A_copy[i * N + k];
            }
            b_copy[j] -= factor * b_copy[i];
        }
    }

    // Back substitution
    for (int i = N - 1; i >= 0; --i) {
        double sum = 0;
        for (size_t j = i + 1; j < N; ++j) {
            sum += A_copy[i * N + j] * x[j];
        }
        x[i] = (b_copy[i] - sum) / A_copy[i * N + i];
    }
}

00_dense_la_lu_decomp:
#pragma once
#include <vector>

/* Factorize the matrix A into A=LU where L is a lower triangular matrix and U is an upper triangular matrix.
   Store the results for L and U into separate matrices L and U. 
   A is an NxN matrix stored in row-major.
   Example:
   input: [[4, 3], [6, 3]]
   output: L=[[1, 0], [1.5, 1]], U=[[4, 3], [0, -1.5]]
*/
void luFactorize(const std::vector<double>& A, std::vector<double>& L, std::vector<double>& U, size_t N) {
    // Initialize L and U matrices
    L.assign(N * N, 0);
    U.assign(N * N, 0);

    for (size_t i = 0; i < N; ++i) {
        for (size_t j = 0; j < N; ++j) {
            if (i == j) {
                L[i * N + j] = 1;  // Diagonal elements of L are 1
            }
            U[i * N + j] = A[i * N + j];  // Copy A into U
        }
    }

    for (size_t k = 0; k < N; ++k) {
        for (size_t i = k + 1; i < N; ++i) {
            double factor = U[i * N + k] / U[k * N + k];
            L[i * N + k] = factor;  // Store the factor in L

            for (size_t j = k; j < N; ++j) {
                U[i * N + j] -= factor * U[k * N + j];
            }
        }
    }
}

