/* For each value in the vector x compute its index in the sorted vector.
   Store the results in `ranks`.
   Examples:
   input: [3.1, 2.8, 9.1, 0.4, 3.14]
   output: [2, 1, 4, 0, 3]
   input: [100, 7.6, 16.1, 18, 7.6]
   output: [4, 0, 1, 2, 3]
*/
void ranks(std::vector<float> const& x, std::vector<size_t> &ranks_) {
    std::vector<std::pair<float, size_t>> indexed_x(x.size());
    for (size_t i = 0; i < x.size(); ++i) {
        indexed_x[i] = {x[i], i};
    }
    std::sort(indexed_x.begin(), indexed_x.end());
    for (size_t i = 0; i < indexed_x.size(); ++i) {
        ranks_[indexed_x[i].second] = i;
    }
}

